drop database if exists biblio;
create database biblio;
use biblio;

CREATE TABLE `Libro` (
  `ISBN` VARCHAR(13) NOT NULL,
  `Titolo` VARCHAR(100) NOT NULL,
  `Autore` VARCHAR(100) NOT NULL,
  `Editore` VARCHAR(100) NOT NULL,
  `Prezzo` FLOAT NOT NULL,
  `Descrizione` VARCHAR(500) NOT NULL,
  `Nome_Categoria` VARCHAR(50) NOT NULL,
  `Sconto` INT DEFAULT 0,
  PRIMARY KEY (`ISBN`));
  
CREATE TABLE `Utente` (
  `Mail` VARCHAR(50) NOT NULL PRIMARY KEY,
  `Password` VARCHAR(40) NOT NULL,
  `Nome` VARCHAR(100) NOT NULL,
  `Cognome` VARCHAR(100),
  `isAdmin` BOOLEAN NULL
  );
  
CREATE TABLE `Preferiti` (
  `ISBN` VARCHAR(13) NOT NULL,
  `Mail` VARCHAR(50) NOT NULL,
  PRIMARY KEY (`ISBN`,`Mail`),
  FOREIGN KEY (`Mail`) REFERENCES `Utente`(`Mail`) ON DELETE CASCADE ON UPDATE CASCADE,
  FOREIGN KEY (`ISBN`) REFERENCES `Libro`(`ISBN`) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE `Login` (
  `id` VARCHAR(36) NOT NULL,
  `mail` VARCHAR(50) NOT NULL,
  `token` CHAR(36) NOT NULL,
  `time` TIMESTAMP NOT NULL,
  PRIMARY KEY (`id`,`mail`),
  FOREIGN KEY (`mail`) REFERENCES `Utente`(`Mail`) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE `Ordine` (
	`id` INT AUTO_INCREMENT NOT NULL,
	`ISBN` VARCHAR(13) NOT NULL,
    `Cliente` VARCHAR(50) NOT NULL,
	PRIMARY KEY(`id`),
	FOREIGN KEY (`ISBN`) REFERENCES `Libro`(`ISBN`) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (`cliente`) REFERENCES `Utente`(`Mail`) ON DELETE CASCADE ON UPDATE CASCADE
);


